import React, { useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function App() {
  const [text, setText] = useState("");
  const [summary, setSummary] = useState([]);
  const [numLines, setNumLines] = useState(2);
  const [darkMode, setDarkMode] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSummarize = async () => {
    if (!text.trim()) {
      toast.error("Please enter some text!");
      return;
    }

    setLoading(true);
    setError(null);
    setSummary([]);

    try {
      const response = await fetch("http://localhost:5000/summarize", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ text, num_sentences: numLines }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.summary || "Failed to summarize");
      }

      const data = await response.json();
      setSummary(data.summary); // expecting an array
      toast.success("Summary generated!");
    } catch (err) {
      setError(err.message || "Something went wrong.");
      toast.error("Error: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={`${darkMode ? "dark" : ""} min-h-screen bg-gray-100 dark:bg-gray-900 transition-colors duration-500`}>
      {/* Toast Container */}
      <ToastContainer position="top-right" autoClose={3000} />

      <div className="max-w-4xl mx-auto p-6">
        {/* Header */}
        <header className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Text Summarizer</h1>
          <button
            onClick={() => setDarkMode(!darkMode)}
            className="bg-gray-300 dark:bg-gray-700 p-2 rounded-md transition"
          >
            {darkMode ? "☀️ Light" : "🌙 Dark"}
          </button>
        </header>

        {/* Textarea */}
        <textarea
          className="w-full p-4 mb-4 rounded-lg border border-gray-300 dark:border-gray-700 resize-none
                   bg-white dark:bg-gray-800 text-gray-900 dark:text-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500"
          rows="6"
          placeholder="Paste or type your text here..."
          value={text}
          onChange={(e) => setText(e.target.value)}
        />

        {/* Number of Lines Selector */}
        <div className="mb-4">
          <label className="text-gray-700 dark:text-gray-300 mr-2">
            How many summary lines?
          </label>
          <select
            value={numLines}
            onChange={(e) => setNumLines(Number(e.target.value))}
            className="p-2 rounded border border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-700 text-gray-900 dark:text-gray-100"
          >
            {[1, 2, 3, 4, 5].map((n) => (
              <option key={n} value={n}>{n}</option>
            ))}
          </select>
        </div>

        {/* Summarize button */}
        <button
          onClick={handleSummarize}
          disabled={!text.trim() || loading}
          className="w-full py-3 bg-blue-600 hover:bg-blue-700 disabled:bg-blue-300 text-white font-semibold rounded-lg shadow-md"
        >
          {loading ? (
            <div className="flex justify-center items-center space-x-2">
              <div className="animate-spin rounded-full h-5 w-5 border-t-2 border-white"></div>
              <span>Summarizing...</span>
            </div>
          ) : (
            "Summarize"
          )}
        </button>

        {/* Error */}
        {error && <div className="mt-4 text-red-600 font-semibold">⚠️ {error}</div>}

        {/* Summary output */}
        {summary.length > 0 && (
          <div className="mt-8 p-6 bg-white dark:bg-gray-800 rounded-lg shadow-lg animate-fadeIn">
            <h2 className="text-xl font-semibold mb-2 text-gray-900 dark:text-gray-100">Summary</h2>
            <p className="text-gray-700 dark:text-gray-300 whitespace-pre-line">

            </p>
          </div>
        )}
      </div>

      {/* Footer */}
      <footer className="mt-16 text-center text-gray-600 dark:text-gray-400 p-4">
        © 2025 Text Summarizer. Made with ❤️
      </footer>

      <style>{`
        @keyframes fadeIn {
          from {opacity: 0;}
          to {opacity: 1;}
        }
        .animate-fadeIn {
          animation: fadeIn 0.8s ease forwards;
        }
      `}</style>
    </div>
  );
}

export default App;
